const Task = ({ task, ...props }) => {
    const ActionBtn = () => <div className="actionBtn">{task.completed?<>✔️</>:<>❌</>}</div>;
    
    
    return(
        <div className="">
            {task.title}
            <ActionBtn></ActionBtn>
    </div>
    )
}

export default Task;
