import React, { useState } from 'react';

class LoginForm extends React.Component{
  render(){
    const {onButtonClick}=this.props; 
    
    onButtonClickLoginCheck = () =>{
        var pass = document.getElementById("website");
        if(pass.value == "correct password") {
            alert("You have logged successfully")
         }
    }




      return <div className="loginBlock">
        <h1>Авторизация</h1>
        <input name="username" id="username" type="text" placeholder="Введите логин"/><br/><br/>
        <input name="website" id="website" type="text" placeholder="Введите пароль"/><br/><br/>
        <button onClick={onButtonClick}>Войти</button>

      </div>;
    }
  }
  
  export default LoginForm;
