const Task = ({task, ...props}) => {
    const ActionBtn = () => <div className="actionBtn">{task.completed?<div onClick={props.doneTask}>✔️</div>:<div onClick={props.deleteTask}>❌</div>}</div>;

    return(
        <div className="task">
            {task.title}<ActionBtn></ActionBtn>
        </div>
    )
}

export default Task;
