import React from "react";
import ToDo from "./ToDo";


class Dashboard extends React.Component{
    handleClick = () =>{
        const {handleLoginClick} =this.props;
        handleLoginClick(false);
        }

  state ={
    todos: [],
    error: ''
 }

 componentDidMount = async() => {
   const urlToDo =`https://jsonplaceholder.typicode.com/todos?userId=${this.props.userId}`;
  
try{
  const result = await fetch(urlToDo)
  const todos = await result.json()
  this.setState({todos});
  
  } catch(err) {
    this.setState({
      error: 'Ошибка'
    })
  } 
} 
toggleCompleted = id => {
  const todos = [...this.state.todos];
  const todo = todos.filter(todo => todo.id === id)[0];
  const index = todos.map(function(e) { return e.id; }).indexOf(id);
  todo.completed = !todo.completed;
  todos[index] = todo;
  this.setState({todos});
}; 
addTodo = todo => {
  const todos = [...this.state.todos];

  todos.push({
      id: todos.length !==0 ? todos[todos.length-1].id + 1 : 0,
      title: todo,
      completed: false
  });

  this.setState({todos});
};

deleteTodo = id => {
  this.setState({
      todos: this.state.todos.filter(todo => todo.id !== id)
  })
};
  render(){
    const {error, todos} =this.state
    const activeTodos = todos.filter(todo => !todo.completed);
        const completedTodos = todos.filter(todo => todo.completed);

    return <div className="Dashboard">
      <h1 className="top">Всего задач: {todos.length}</h1>
      
      <h2>{error}</h2>
      {[...activeTodos,...completedTodos].map(todo =>
                        <Todo
                            todo = {todo}
                            key={todo.id}
                            toggleCompleted={() => this.toggleCompleted(todo.id)}
                            deleteTodo={() => this.deleteTodo(todo.id)}
                        />)}<br></br>
     <button onClick={this.handleClick} className="exit">Выйти</button>
    </div>
  }
}

export default Dashboard;
