import React from "react";
import ToDo from "./ToDo";
const urlToDo ='https://jsonplaceholder.typicode.com/todos?userId=1'

class Dashboard extends React.Component{
    handleClick = () =>{
        const {handleLoginClick} =this.props;
        handleLoginClick(false);
        }

  state ={
    todos: [],
    error: ''
 }

 componentDidMount = async() => {
  let {todos} = [];
try{
  const result = await fetch(urlToDo)
  todos = await result.json()
  } catch(err) {
    this.setState({
      error: 'Ошибка'
    })
  }

  this.setState({
    todos,
  })
} 
  render(){
    const {error, todos} =this.state

    return <div className="Dashboard">
      <h1 className="top">Actice tasks: {todos.length}</h1>
      <button onClick={this.handleClick}>Выйти</button>
      <h2>{error}</h2>
     {todos.map(task =>(<ToDo task={task} key={task.id}></ToDo>
       )) 
     }
    </div>
  }
}

export default Dashboard;
