const Task = ({task,...props}) =>{
    const ActionBtn = () => <div>{task.done?<p>✔️</p>:<p>❌</p>}</div>;
    return(
        <div>
            <p>{task.title}</p>
            <ActionBtn></ActionBtn>
        </div>
    )
}

export default Task;
