const Task = ({ task, ...props }) => {
    const ActionBtn = () => <div className="actionBtn">{task.completed?<span aria-label="done" role="img" onClick={props.toggleCompleted}>✔️</span>:
    <span aria-label="delete" role="img" onClick={props.deleteTask}>❌</span>}</div>;
    
    
    return(
        <div className="">
            {task.title}
            <ActionBtn></ActionBtn>
    </div>
    )
}

export default Task;
