const ToDo = ({task, ...props}) => {
    const ActionBtn = () => <div className="actionBtn">{task.completed?<>✔️</>:<>❌</>}</div>;
    const className = 'task ' + (task.done ? 'task-done' : '');
    return(
        <div className="task">
            {task.title}<ActionBtn></ActionBtn>
        </div>
    )
}

export default ToDo;
