import React, { useState } from 'react'
import './index.css';
import LoginForm from './components/LoginForm';
import Dashboard from './components/Dashboard';


class App extends React.Component{
  state ={
    isLoggedIn: 0,
    userId: null,
  }

  handleLoginClick =(param) =>{
    this.setState({ isLoggedIn: param});

  }
  setUserId = userId => {
    this.setState({userId})
  }


  render(){

    return (
      <div className="App">
        {this.state.isLoggedIn? <div className="MainScreen">
          <Dashboard handleLoginClick={this.handleLoginClick}/>
        </div>: <LoginForm handleLoginClick={this.handleLoginClick} setUserId={this.setUserId} />}
      </div>
    )
  }
}

export default App;
