import React from "react";
import ToDo from './components/ToDo';
const urlToDo ='https://jsonplaceholder.typicode.com/todos'

class App extends React.Component{
  state ={
    todos: [],
    error: ''
 }

 componentDidMount = async() => {
  let {todos} = [];
try{
  const result = await fetch(urlToDo)
  todos = await result.json()
  } catch(err) {
    this.setState({
      error: 'Ошибка'
    })
  }

  this.setState({
    todos,
  })
} 
  render(){
    const {error, todos} =this.state

    return <div className="App">
      <h1 className="top">Actice tasks: {todos.length}</h1>

     {todos.map(task =>(<ToDo task={task} key={task.id}></ToDo>
       )) 
     }
    </div>
  }
}

export default App;
