import React, { useState } from 'react';

class LoginForm extends React.Component{   
    
  state = {
    username: '',
    pass: '',
    isFailedLogIn: false
}
handleLoginClick = async (e) => {
  e.preventDefault();

  const username = this.state.username;
  const pass = this.state.pass;

  const requestUrl = `https://jsonplaceholder.typicode.com/users?username=${username}&website=${pass}`;

  const responseResult = await fetch(requestUrl);
  const userJson = await responseResult.json();

  const {handleLoginClick} = this.props;
  const {setUserId} = this.props;
  if(userJson[0] !== undefined) {
      setUserId(userJson[0].id);
      handleLoginClick(true);
  }
  else {
      this.setState({ isFailedLogIn: true })
  }
}

  render(){
    const {onButtonClick}=this.props;        

          return <div className="loginBlock">
        <h1>Авторизация</h1>
        <input name="username" id="username" type="text" placeholder="Введите логин"/><br/><br/>
        <input name="website" id="website" type="text" placeholder="Введите пароль"/><br/><br/>
        <button onClick={this.handleLoginClick}>Войти</button>

      </div>;
    }
  }
  
  export default LoginForm;
