const Task = ({task,...props}) =>{
    const ActionBtn = () => <div className="action-btn">{task.done?(
        <span aria-label="done" role="img" onClick={props.doneTask}>
          ✔️
        </span>
      ) : (
        <span aria-label="delete" role="img" onClick={props.deleteTask}>
          ❌
        </span>
      )}</div>;
    return(
        <div>
            <p>{task.title}</p>
            <ActionBtn></ActionBtn>
        </div>
    )
}

export default Task;
