const ToDo = ({todo, ...props}) => {
    const ActionBtn = () => <div className="actionBtn">{todo.completed?<div onClick={props.doneTask}>✔️</div>:<div onClick={props.deleteTask}>❌</div>}</div>;

    

    return(
        <div className="task">
            {todo.title}<ActionBtn></ActionBtn>
        </div>
    )
}

export default ToDo;
