class Store extends React.Component{
  
  doneTask(id) {
    let tasks = this.tasks;
    const index = tasks.map(task => task.id).indexOf(id);
    tasks[index].done = true;
    this.setTasks(tasks);
  }

  deleteTask(id) {
    this.setTasks(this.tasks.filter(item => item.id !== id));
  }
}



export default Store();
