const Task = ({ task, ...props }) => {
    const ActionBtn = () => <div className="actionBtn">{task.completed?
    <span onClick={props.toggleCompleted}>✔️</span>:
    <span onClick={props.toggleCompleted}>❌</span>}
    </div>;

    return(
        <div className="">
            <span className="close" onClick={props.deleteTodo}>×</span>{task.title}<span className="close" onClick={props.deleteTodo}>×</span>
            <ActionBtn></ActionBtn>

    </div>
    )
}

export default Task;
