import React from "react";
import Task from './components/Task';

class App extends React.Component{
  constructor(){
    super();

    this.state = {
      tasks: [
        {id: 0, title: 'CreateApp', done: false},
        {id: 1, title: 'CreateApp', done: true},
        {id: 2, title: 'CreateApp', done: false}]
    };
  }

  render(){
    const { tasks } = this.state;

    return <div className="App">
      <h1 className="top">Actice tasks: {tasks.length}</h1>

     {tasks.map(task =>(<Task task={task} key={task.id}></Task>
       )) 
     }
    </div>
  }
}

export default App;
